export const localStorageMock = {
  store: {},
  getItem: (key) => {
    return localStorageMock.store[key];
  },
  setItem: (key, value) => {
    localStorageMock.store[key] = value.toString();
  },
  clear: () => {
    localStorageMock.store = {};
  },
  removeItem: (key) => {
    delete localStorageMock.store[key];
  }
};
