import { defineComponent } from "vue";
import { uid } from "@inkline/inkline/helpers";
const componentName = "ITab";
export default defineComponent({
  name: componentName,
  inject: {
    tabs: {
      default: () => ({})
    }
  },
  props: {
    /**
     * The title of the tab
     * @type String
     * @default
     * @name title
     */
    title: {
      type: String,
      default: ""
    },
    /**
     * The name of the tab, used as an identifier
     * @type String
     * @default uid()
     * @name name
     */
    name: {
      type: String,
      default() {
        return uid("tab");
      }
    }
  },
  computed: {
    active() {
      return this.tabs.active === this.name;
    },
    classes() {
      return {
        "-active": this.active
      };
    }
  }
});
