import { defineComponent } from "vue";
import { uid } from "@inkline/inkline/helpers";
const componentName = "ITabTitle";
export default defineComponent({
  name: componentName,
  inject: {
    tabs: {
      default: () => ({})
    }
  },
  props: {
    /**
     * The name of the referenced tab
     * @type String
     * @default
     * @name for
     */
    for: {
      type: String,
      default() {
        return uid("tab");
      }
    }
  },
  computed: {
    active() {
      return this.tabs.active === this.for;
    },
    classes() {
      return {
        "-active": this.active
      };
    },
    name() {
      return this.for;
    }
  },
  methods: {
    onClick() {
      this.tabs.setActive(this.for);
    }
  }
});
