import { c as consola } from '../shared/nuxi-edge.114b1a98.mjs';
import 'node:util';
import 'node:path';
import 'node:process';
import 'tty';
import { c as clearBuildDir } from '../shared/nuxi-edge.4960b86f.mjs';
import { l as loadKit } from '../shared/nuxi-edge.18a0cc22.mjs';
import { w as writeTypes } from '../shared/nuxi-edge.dfd5586d.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi-edge.c68ce99d.mjs';
import { r as resolve, a as relative } from '../shared/nuxi-edge.ffb4843d.mjs';
import 'node:fs';
import '../shared/nuxi-edge.fd810ae3.mjs';
import 'node:url';
import '../shared/nuxi-edge.5e7337fa.mjs';
import 'node:module';
import 'node:assert';
import 'node:v8';
import '../shared/nuxi-edge.a685c563.mjs';
import '../shared/nuxi-edge.074bdf6d.mjs';

const prepare = defineNuxtCommand({
  meta: {
    name: "prepare",
    usage: "npx nuxi prepare [--log-level] [rootDir]",
    description: "Prepare nuxt for development/build"
  },
  async invoke(args) {
    process.env.NODE_ENV = process.env.NODE_ENV || "production";
    const rootDir = resolve(args._[0] || ".");
    const { loadNuxt, buildNuxt } = await loadKit(rootDir);
    const nuxt = await loadNuxt({
      rootDir,
      overrides: {
        _prepare: true,
        logLevel: args["log-level"]
      }
    });
    await clearBuildDir(nuxt.options.buildDir);
    await buildNuxt(nuxt);
    await writeTypes(nuxt);
    consola.success("Types generated in", relative(process.cwd(), nuxt.options.buildDir));
  }
});

export { prepare as default };
