import { e as execa } from '../shared/nuxi-edge.ecb73dbd.mjs';
import { t as tryResolveModule } from '../shared/nuxi-edge.bd72309b.mjs';
import { l as loadKit } from '../shared/nuxi-edge.1be3df56.mjs';
import { w as writeTypes } from '../shared/nuxi-edge.a10a8e78.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi-edge.c68ce99d.mjs';
import { r as resolve } from '../shared/nuxi-edge.ffb4843d.mjs';
import 'node:buffer';
import 'node:path';
import 'node:child_process';
import 'node:process';
import '../shared/nuxi-edge.fe74a616.mjs';
import 'child_process';
import 'path';
import '../shared/nuxi-edge.849bcf65.mjs';
import 'fs';
import 'assert';
import 'events';
import 'buffer';
import 'stream';
import 'util';
import 'node:url';
import 'node:os';
